// WEB SOCKET (node-red)
var ws;
var wsUri = "ws:";
var loc = window.location; //console.log(loc);

if (loc.protocol === "https:") { wsUri = "wss:"; }
// This needs to point to the web socket in the Node-RED flow
wsUri += "//" + loc.host + loc.pathname.replace("worklet", "ws/worklet");

function wsConnect() {

    console.log("Connecting to ", wsUri);
    ws = new WebSocket(wsUri);

    ws.onmessage = function (msg) {

        // parse the incoming message as a JSON object
        var data = msg.data;
        const obj = JSON.parse(data);

        //console.log("Received WS message: " + JSON.stringify(obj));

        //PopulateActions(obj);

        location.reload();

        /*
        fetch('/worklet', {
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({ "id": 78912 })
        });*/

        //ws.send(JSON.stringify({data:data}));
    }

    ws.onopen = function () {
        // update the status div with the connection status
        console.log("Connected to ws/worklet");
    }

    ws.onclose = function () {
        // update the status div with the connection status
        console.log("ws/worklet connection lost");
        // in case of lost connection tries to reconnect every 3 secs
        setTimeout(wsConnect, 3000);
    }
}

function ApproveWorkLetLogin() {
    let loginObj = {
        "user": loginEmail.value,
        "pass": loginPass.value
    }
    if (ws){
        ws.send(JSON.stringify(loginObj));
        }
}

function StartRun() {
    let mqttObj = {
        "mqtt": "start"
    }
    if (ws) {
        ws.send(JSON.stringify(mqttObj));
    }
}

