function togglePop(element)
{
    element.classList.toggle("show");
}


const menuElements = document.getElementsByClassName("menuItem");
const listElements = document.getElementsByClassName("listItem");

var currentDraggedElement = null;

function dragItem(itemId)
{
    var item = document.getElementById(itemId);
    currentDraggedElement = item;

    let itemX = item.offsetLeft;
    let itemY = item.offsetTop;
    //console.log("Item pos: [" + itemX + ", " + itemY + "]");
    //console.log("Mouse pos: [" + mouseX + ", " + mouseY + "]");
    //console.log("Offset: [" + (itemX-mouseX) + ", " + (itemY-mouseY) + "]");

    offsetX = (itemX-mouseX);
    offsetY = (itemY-mouseY) - 25;
}

function dropZone(zone)
{
    if(currentDraggedElement == null)
        return;

    console.log("Item '" + currentDraggedElement.id + "' was dropped into zone '" + zone.id + "'");

    clearGhost();
}



var ghostObject = null;
const ghost = document.querySelector('.ghost');

function createGhostObject(item)
{
    ghostObject = item;
    ghost.innerHTML = ghostObject.innerHTML;
    ghost.style = ghostObject.style;
    ghost.style.marginLeft = offsetX + "px";
    ghost.style.marginTop = offsetY + "px";
    ghost.classList.add("visible");

    item.style.opacity = 0.6;

    document.querySelector('body').style.cursor = "grabbing";
}

var mouseX, mouseY;
var offsetX, offsetY;
document.addEventListener('mousemove', function(e)
{            
    let left = mouseX = e.pageX;
    let top = mouseY = e.pageY;

    if(currentDraggedElement == null)
        return;

    if(ghostObject == null)
        createGhostObject(currentDraggedElement);

    ghost.style.left = left + 'px';
    ghost.style.top = top + 'px';
});

document.addEventListener('mouseup', function(e)
{
    if(currentDraggedElement == null)
        return;
    clearGhost();
});

function clearGhost()
{
    currentDraggedElement.style.opacity = 1;
    currentDraggedElement = null;

    ghostObject = null;            
    ghost.innerHTML = "";
    ghost.classList.remove("visible");

    document.querySelector('body').style.cursor = "auto";
    //ghost.style = null;
}

