if (_citizenList != "")
    citizenList = JSON.parse(_citizenList);


//
/// UI STATE VARS + FUNCTIONS
//

var isWorking = false;
var hasWorked = false;

var pageAccepted = [];
pageAccepted['login'] = false;
pageAccepted['rules'] = false;
pageAccepted['grants'] = false;

var _loginAccepted = "{{{flow.loginAccepted}}}"; if (_loginAccepted == "true") togglePageAccept("login");
var _rulesAccepted = "{{{flow.rulesAccepted}}}"; if (_rulesAccepted == "true") togglePageAccept("rules");
var _grantsAccepted = "{{{flow.grantsAccepted}}}"; if (_grantsAccepted == "true") togglePageAccept("grants");

function togglePageAccept(page, setAccept = true)
{
    var title;
    switch (page) {
        case "login": title = "Loginoplysninger"; break;
        case "rules": title = "Forretningsregler"; break;
        case "grants": title = "Tilskudssatser"; break;
    }

    if (setAccept)
        pageAccepted[page] = !pageAccepted[page];

    if (pageAccepted[page]) {
        setMenuItemGreen("menu_" + page);

        if (activePage != page)
            return;

        let pageCap = page.charAt(0).toUpperCase() + page.slice(1);
        setInnerHTML("button" + pageCap, title + " godkendt");
        setButtonGreen("button" + pageCap);
        enableGoNext("button" + pageCap + "_goNext");
        document.getElementById("toAccept").value = "false";
    }
    else {
        setMenuItemNormal("menu_" + page);

        if (activePage != page)
            return;

        let pageCap = page.charAt(0).toUpperCase() + page.slice(1);
        setInnerHTML("button" + pageCap, "Godkend " + title.toLowerCase());
        setButtonNormal("button" + pageCap);
        disableGoNext("button" + pageCap + "_goNext");
        document.getElementById("toAccept").value = "true";
    }
}

function withdrawPageAccept(page) {
    if (pageAccepted[page])
        togglePageAccept(page);
}




//
/// ON PAGE LOAD FUNCTIONS
//

var loadPageFunc = [];

loadPageFunc["rules"] = function ()
{    
    // LOAD rules - update each rule
    for (let index = 0; index < rules.length; index++)
    {
        const element = rules[index];

        const operatorInput = document.getElementById("operator_" + element.uid);
        var valueInput = document.getElementById("value_" + element.uid);

        if (element.operator == "!null")
        {
            setInputBox(element.uid, true);
            valueInput = document.getElementById("value_" + element.uid);
        }

        operatorInput.value = element.operator;
        valueInput.value = element.value;
    }
}

loadPageFunc["receipts"] = function ()
{
    if(isWorking)
        visualizeWorkingState();

    else if(hasWorked || citizenList.length > 0)
        visualizeFinishedState();

    else if (pageAccepted["login"] && pageAccepted["rules"] && pageAccepted["grants"])
        document.getElementById("buttonReceipts").disabled = false;
}

loadPageFunc["receipts-recommended"] = function ()
{
    if(citizenList.length > 0)
        loadReceipts();
}

//
/// SET PAGE CONTENT FUNCTION
//

var activePage = null;
function setPageContent(page, setHistory = true)
{
    //console.log("Setting page content to page: " + page);

    if (pages[page] == null)
    {
        setPageContent("404", false);
        return;
    }
    else if (activePage == page)
        return;

    let menuItem = document.getElementById("menu_" + activePage);
    menuItem?.classList.remove("selected");
    menuItem?.classList.remove("bg-dark");
    menuItem?.classList.add("text-primary");
    menuItem?.classList.remove("text-white");

    menuItem = document.getElementById("menu_" + page);
    menuItem?.classList.add("selected");
    menuItem?.classList.add("bg-dark");
    menuItem?.classList.remove("text-primary");
    menuItem?.classList.add("text-white");

    setInnerHTML("mainContent", pages[page]);
    document.title = "AutoWorkLet - " + page;

    if(setHistory)
        window.history.pushState({ "page": page }, "", "/" + page);

    activePage = page;

    if (page == "login" || page == "rules" || page == "grants")
        togglePageAccept(page, false);

    if(loadPageFunc[page] != null)
        loadPageFunc[page]();

    initialLoad = false;

}

// START / ON LOAD

setPageContent(landingPage);

window.onpopstate = function (e)
{
    if (e.state)
        setPageContent(e.state.page, false);
};



