function enableGoNext(objectId)
{
    var button = document.getElementById(objectId);

    if (!button.classList.contains("collapsed"))
        return;

    button.classList.remove("collapsed");
}
function disableGoNext(objectId)
{
    var button = document.getElementById(objectId);

    if (button.classList.contains("collapsed"))
        return;

    button.classList.add("collapsed");
}

function setButtonGreen(objectId)
{
    var button = document.getElementById(objectId);

    if (button.classList.contains("btn-success"))
        return;

    button.classList.remove("btn-primary");
    button.classList.add("btn-success");
}

function setButtonNormal(objectId)
{
    var button = document.getElementById(objectId);

    if (button.classList.contains("btn-primary"))
        return;

    button.classList.remove("btn-success");
    button.classList.add("btn-primary");
}

function setMenuItemGreen(objectId)
{
    var menuItem = document.getElementById(objectId);

    if(menuItem.classList.contains("success"))
        return;

    menuItem.classList.add("success");

}
function setMenuItemNormal(objectId)
{
    var menuItem = document.getElementById(objectId);

    if (!menuItem.classList.contains("success"))
        return;

    menuItem.classList.remove("success");
}

function toggleListItemActive(objectId)
{
    var listItem = document.getElementById(objectId);
    listItem.classList.toggle("text-primary");
    listItem.classList.toggle("disabled");
}




