function postRequest(text)
{
    fetch('/js/post', {
        method: 'POST',
        headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json'
        },
        body: JSON.stringify({ "text": text })
    })
        .then(response => response.json())
        .then(response => handlePostResponse(response))
        .then(response => console.log(JSON.stringify(response)));
}


function handlePostResponse(responseObject)
{
    setInnerHTML("responseDiv", responseObject.text);
    return responseObject;
}


function setInnerHTML(objectName, content) {
    var container = document.getElementById(objectName);
    container.innerHTML = content;
}

function postSetRequest(variable, text)
{
    fetch('/ajs/set', {
        method: 'POST',
        headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json'
        },
        body: JSON.stringify({ "variable": variable, "value": text })
    })
        .then(response => response.json())
        .then(response => handlePostResponse(response))
        .then(response => console.log(JSON.stringify(response)));
}

