function handlePostResponse(responseObject)
{
    //console.log("Attempting to call response function " + responseObject.responseFunction);
    //console.log("Function: " + handleResponseDynamically[responseObject.responseFunction]);


    if (handleResponseDynamically[responseObject.responseFunction] != null)
        handleResponseDynamically[responseObject.responseFunction](responseObject);

    return responseObject;
}

var handleResponseDynamically = [];

handleResponseDynamically['login'] = function (responseObj)
{
    if (responseObj.response)
        togglePageAccept("login");
}

handleResponseDynamically['rules'] = function (responseObj)
{
    if (!responseObj.response)
        return;

    togglePageAccept("rules");

    if(responseObj.rules == null)
        return;

    rules = responseObj.rules;
}

handleResponseDynamically['grants'] = function (responseObj) {
    if (responseObj.response)
        togglePageAccept("grants");
}

//
